mapper.select:
select b_no, b_title, b_content, b_writer, b_write_date, b_count
from tb_board 
where b_no = #{b_no}

mapper.update:
update tb_board 
set b_count = b_count+1 
where b_no = #{b_no}

update tb_board
set b_title = #{b_title}, b_content = #{b_content}
where b_no = #{b_no}

mapper.insert:
insert into tb_comment (c_no , b_no, m_id, c_writer, c_write_date, c_comment , c_level , c_ref , c_seq) values ((select nvl(max(c_no), 0)+1 from tb_comment) , #{b_no}, #{m_id}, #{c_writer}, sysdate, #{c_comment}
, (select c_level+1 from tb_comment where c_no=#{refnum}) , #{refnum} , (select c_seq+1 from tb_comment where c_no=#{refnum})
, default , (select nvl(max(c_no), 0)+1 from tb_comment) , default
)

mapper.update:
update tb_comment set c_seq = c_seq+1
where c_ref = #{refnum}
and c_seq > (select c_seq from tb_comment where c_no=#{refnum})

mapper.select:
select * from tb_comment
where b_no = #{b_no}
order by c_ref asc, c_write_date asc ,c_seq desc

mapper.update:
update tb_comment
set c_comment = #{c_comment}
where c_no = #{c_no}
